  .logo {
    height: 50px;
  }

  .nav {
    gap: 1.5rem;
  }

  .nav-link {
    font-size: 1rem;
  }

  .header-title {
    font-size: 1.75rem;
  }

  .header-subtitle {
    font-size: 1rem;
  }
}

