from flask import Flask, jsonify, send_from_directory
from flask_cors import CORS
import os

app = Flask(__name__, static_folder='../frontend/build')
CORS(app)

# Configuração
WHATSAPP_NUMBER = '5511992891070'

# Rotas API
@app.route('/api/config', methods=['GET'])
def get_config():
    """Retorna configurações do site"""
    return jsonify({
        'title': 'Cortinas sob medida em São Paulo',
        'description': 'Transforme seu ambiente com sofisticação, tecidos nobres e instalação profissional.',
        'whatsapp': WHATSAPP_NUMBER,
        'address': {
            'street': 'Av. Celso Garcia, 129',
            'neighborhood': 'Brás',
            'city': 'São Paulo',
            'state': 'SP'
        },
        'contact': {
            'phone': '(11) 99289-1070',
            'email': 'contato@cortinasbras.com.br'
        }
    })

@app.route('/api/slides', methods=['GET'])
def get_slides():
    """Retorna slides do carrossel"""
    return jsonify([
        {'src': 'slide1.jpg', 'alt': 'Cortina elegante em sala de estar'},
        {'src': 'slide2.jpg', 'alt': 'Cortina moderna em quarto'},
        {'src': 'slide3.jpg', 'alt': 'Cortina sofisticada em escritório'}
    ])

# Serve React App
@app.route('/', defaults={'path': ''})
@app.route('/<path:path>')
def serve(path):
    if path != "" and os.path.exists(app.static_folder + '/' + path):
        return send_from_directory(app.static_folder, path)
    else:
        return send_from_directory(app.static_folder, 'index.html')

if __name__ == '__main__':
    app.run(debug=True, host='0.0.0.0', port=5000)

import React, { useState, useEffect } from 'react';
import './App.css';
import Header from './components/Header';
import Carousel from './components/Carousel';
import Hero from './components/Hero';
import QuoteForm from './components/QuoteForm';
import Map from './components/Map';
import Footer from './components/Footer';

function App() {
  const [config, setConfig] = useState(null);
  const [slides, setSlides] = useState([]);

  useEffect(() => {
    // Fetch config
    fetch('/api/config')
      .then(res => res.json())
      .then(data => setConfig(data))
      .catch(err => console.error('Error loading config:', err));

    // Fetch slides
    fetch('/api/slides')
      .then(res => res.json())
      .then(data => setSlides(data))
      .catch(err => console.error('Error loading slides:', err));
  }, []);

  if (!config) {
    return <div className="loading">Carregando...</div>;
  }

  return (
    <div className="App">
      <Header config={config} />
      <Carousel slides={slides} />
      <main>
        <Hero />
        <QuoteForm whatsappNumber={config.whatsapp} />
        <Map address={config.address} />
      </main>
      <Footer config={config} />
    </div>
  );
}

export default App;

import React from 'react';
import './Header.css';

function Header({ config }) {
  return (
    <header className="header">
      <div className="header-top">
        <div className="logo-container">
          <img src="/logobras.png" alt="Logo Cortinas Brás" className="logo" />
        </div>
        <nav className="nav">
          <a href="#form-orcamento" className="nav-link">Orçamento</a>
          <a href="#contato" className="nav-link">Contato</a>
        </nav>
      </div>
      <div className="header-content">
        <h1 className="header-title">{config.title}</h1>
        <p className="header-subtitle">{config.description}</p>
      </div>
    </header>
  );
}

export default Header;

import React, { useState, useEffect } from 'react';
import './Carousel.css';

function Carousel({ slides }) {
  const [currentSlide, setCurrentSlide] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 5000);
    return () => clearInterval(interval);
  }, [slides.length]);

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % slides.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + slides.length) % slides.length);
  };

  if (!slides.length) return null;

  return (
    <section className="carousel">
      <div className="carousel-container">
        {slides.map((slide, index) => (
          <div
            key={index}
            className={`carousel-slide ${index === currentSlide ? 'active' : ''}`}
          >
            <img src={`/${slide.src}`} alt={slide.alt} />
          </div>
        ))}
      </div>
      <button className="carousel-btn prev" onClick={prevSlide} aria-label="Slide anterior">
        &#10094;
      </button>
      <button className="carousel-btn next" onClick={nextSlide} aria-label="Próximo slide">
        &#10095;
      </button>
      <div className="carousel-indicators">
        {slides.map((_, index) => (
          <button
            key={index}
            className={`indicator ${index === currentSlide ? 'active' : ''}`}
            onClick={() => setCurrentSlide(index)}
            aria-label={`Slide ${index + 1}`}
          />
        ))}
      </div>
    </section>
  );
}

export default Carousel;

import React from 'react';
import './Hero.css';

function Hero() {
  const scrollToForm = () => {
    document.getElementById('form-orcamento')?.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <section className="hero">
      <div className="hero-content">
        <h2 className="hero-title">Decore seu ambiente com tradição, referência em SP</h2>
        <p className="hero-description">Solicite um orçamento rápido e personalizado!</p>
        <button className="btn-primary" onClick={scrollToForm}>
          Quero meu orçamento
        </button>
      </div>
    </section>
  );
}

export default Hero;

import React, { useState } from 'react';
import './QuoteForm.css';

function QuoteForm({ whatsappNumber }) {
  const [formData, setFormData] = useState({
    nome: '',
    telefone: '',
    largura: '',
    altura: ''
  });
  const [showSuccess, setShowSuccess] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const mensagem = `Olá! Gostaria de solicitar um orçamento:

*Nome:* ${formData.nome}
*Telefone:* ${formData.telefone}
*Largura da parede:* ${formData.largura}m
*Altura da parede:* ${formData.altura}m

Aguardo retorno!`;

    const mensagemCodificada = encodeURIComponent(mensagem);
    const urlWhatsApp = `https://wa.me/${whatsappNumber}?text=${mensagemCodificada}`;

    window.open(urlWhatsApp, '_blank');

    setShowSuccess(true);
    setFormData({ nome: '', telefone: '', largura: '', altura: '' });

    setTimeout(() => {
      setShowSuccess(false);
    }, 5000);
  };

  return (
    <section className="form-section" id="form-orcamento">
      <div className="form-container">
        <h3 className="form-title">Peça seu orçamento em poucos minutos!</h3>
        <form onSubmit={handleSubmit} className="quote-form">
          <div className="form-group">
            <label htmlFor="nome">Nome</label>
            <input
              type="text"
              id="nome"
              name="nome"
              value={formData.nome}
              onChange={handleChange}
              placeholder="Seu nome"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="telefone">WhatsApp</label>
            <input
              type="tel"
              id="telefone"
              name="telefone"
              value={formData.telefone}
              onChange={handleChange}
              placeholder="(11) 99999-9999"
              required
            />
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="largura">
                Largura da parede (m) <span className="input-example">Ex: 1,5</span>
              </label>
              <input
                type="text"
                id="largura"
                name="largura"
                value={formData.largura}
                onChange={handleChange}
                placeholder="Ex: 1,5"
                pattern="[0-9]+([\.,][0-9]+)?"
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="altura">
                Altura da parede (m) <span className="input-example">Ex: 1,5</span>
              </label>
              <input
                type="text"
                id="altura"
                name="altura"
                value={formData.altura}
                onChange={handleChange}
                placeholder="Ex: 1,5"
                pattern="[0-9]+([\.,][0-9]+)?"
                required
              />
            </div>
          </div>

          <button type="submit" className="btn-primary btn-submit">
            Solicitar orçamento
          </button>
        </form>

        {showSuccess && (
          <div className="success-message">
            ✓ Abrindo WhatsApp com sua solicitação!
          </div>
        )}
      </div>
    </section>
  );
}

export default QuoteForm;

import React from 'react';
import './Map.css';

function Map({ address }) {
  const addressString = `${address.street}+-+${address.neighborhood},+${address.city}-${address.state}`;

  return (
    <section className="map-section">
      <h3 className="map-title">Como chegar</h3>
      <div className="map-container">
        <iframe
          src={`https://www.google.com/maps?q=${addressString}&output=embed`}
          width="100%"
          height="400"
          style={{ border: 0 }}
          allowFullScreen=""
          loading="lazy"
          referrerPolicy="no-referrer-when-downgrade"
          title="Mapa de localização"
        />
      </div>
    </section>
  );
}

export default Map;

import React from 'react';
import './Footer.css';

function Footer({ config }) {
  return (
    <footer className="footer" id="contato">
      <div className="footer-content">
        <address className="footer-address">
          {config.address.street} - {config.address.neighborhood}, {config.address.city}-{config.address.state}
          <br />
          WhatsApp: {config.contact.phone}
          <br />
          E-mail:{' '}
          <a href={`mailto:${config.contact.email}`} className="footer-link">
            {config.contact.email}
          </a>
        </address>
        <p className="footer-copyright">© 2025 Cortinas Brás - Todos os direitos reservados.</p>
      </div>
    </footer>
  );
}

export default Footer;

:root {
  --primary: #b8860b;
  --primary-dark: #8b6914;
  --primary-light: #daa520;
  --bg-dark: #0a0a0a;
  --bg-darker: #050505;
  --text: #ffffff;
  --text-muted: #b0b0b0;
  --border: rgba(255, 255, 255, 0.1);
  --shadow: 0 4px 6px rgba(0, 0, 0, 0.3);
}

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Oxygen',
    'Ubuntu', 'Cantarell', 'Fira Sans', 'Droid Sans', 'Helvetica Neue', sans-serif;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  background: var(--bg-dark);
  color: var(--text);
  line-height: 1.6;
}

.App {
  min-height: 100vh;
  display: flex;
  flex-direction: column;
}

main {
  flex: 1;
}

.loading {
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  font-size: 1.5rem;
  color: var(--primary);
}

.btn-primary {
  background: linear-gradient(135deg, var(--primary), var(--primary-dark));
  color: white;
  border: none;
  padding: 1rem 2.5rem;
  font-size: 1.1rem;
  font-weight: 600;
  border-radius: 50px;
  cursor: pointer;
  transition: all 0.3s ease;
  box-shadow: var(--shadow);
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.btn-primary:hover {
  transform: translateY(-2px);
  box-shadow: 0 6px 12px rgba(184, 134, 11, 0.4);
  background: linear-gradient(135deg, var(--primary-light), var(--primary));
}

.btn-primary:active {
  transform: translateY(0);
}

@media (max-width: 768px) {
  .btn-primary {
    padding: 0.875rem 2rem;
    font-size: 1rem;
  }
}

.header {
  background: linear-gradient(180deg, var(--bg-darker), var(--bg-dark));
  border-bottom: 2px solid var(--primary);
  padding: 1.5rem 2rem;
}

.header-top {
  max-width: 1200px;
  margin: 0 auto;
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 2rem;
}

.logo-container {
  flex-shrink: 0;
}

.logo {
  height: 60px;
  width: auto;
}

.nav {
  display: flex;
  gap: 2rem;
}

.nav-link {
  color: var(--text);
  text-decoration: none;
  font-weight: 500;
  font-size: 1.1rem;
  transition: color 0.3s ease;
  position: relative;
}

.nav-link::after {
  content: '';
  position: absolute;
  bottom: -5px;
  left: 0;
  width: 0;
  height: 2px;
  background: var(--primary);
  transition: width 0.3s ease;
}

.nav-link:hover {
  color: var(--primary);
}

.nav-link:hover::after {
  width: 100%;
}

.header-content {
  max-width: 1200px;
  margin: 0 auto;
  text-align: center;
}

.header-title {
  font-size: 2.5rem;
  font-weight: 700;
  margin-bottom: 1rem;
  background: linear-gradient(135deg, var(--primary-light), var(--primary));
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.header-subtitle {
  font-size: 1.25rem;
  color: var(--text-muted);
  max-width: 700px;
  margin: 0 auto;
}

@media (max-width: 768px) {
  .header {
    padding: 1rem;
  }

  .header-top {
    flex-direction: column;
    gap: 1rem;
  }

  .logo {
    height: 50px;
  }

  .nav {
    gap: 1.5rem;
  }

  .nav-link {
    font-size: 1rem;
  }

  .header-title {
    font-size: 1.75rem;
  }

  .header-subtitle {
    font-size: 1rem;
  }
}

.carousel {
  position: relative;
  width: 100%;
  max-width: 1200px;
  margin: 2rem auto;
  height: 500px;
  overflow: hidden;
  border-radius: 12px;
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.5);
}

.carousel-container {
  position: relative;
  width: 100%;
  height: 100%;
}

.carousel-slide {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  opacity: 0;
  transition: opacity 0.8s ease-in-out;
}

.carousel-slide.active {
  opacity: 1;
}

.carousel-slide img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.carousel-btn {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  background: rgba(0, 0, 0, 0.6);
  color: var(--primary);
  border: 2px solid var(--primary);
  width: 50px;
  height: 50px;
  border-radius: 50%;
  font-size: 1.5rem;
  cursor: pointer;
  transition: all 0.3s ease;
  z-index: 10;
  display: flex;
  align-items: center;
  justify-content: center;
}

.carousel-btn:hover {
  background: rgba(184, 134, 11, 0.9);
  color: white;
}

.carousel-btn.prev {
  left: 20px;
}

.carousel-btn.next {
  right: 20px;
}

.carousel-indicators {
  position: absolute;
  bottom: 20px;
  left: 50%;
  transform: translateX(-50%);
  display: flex;
  gap: 10px;
  z-index: 10;
}

.indicator {
  width: 12px;
  height: 12px;
  border-radius: 50%;
  border: 2px solid white;
  background: transparent;
  cursor: pointer;
  transition: all 0.3s ease;
  padding: 0;
}

.indicator.active {
  background: var(--primary);
  border-color: var(--primary);
  transform: scale(1.2);
}

.indicator:hover {
  background: rgba(255, 255, 255, 0.5);
}

@media (max-width: 768px) {
  .carousel {
    height: 300px;
    margin: 1rem;
  }

  .carousel-btn {
    width: 40px;
    height: 40px;
    font-size: 1.2rem;
  }

  .carousel-btn.prev {
    left: 10px;
  }

  .carousel-btn.next {
    right: 10px;
  }
}

.hero {
  background: linear-gradient(135deg, rgba(184, 134, 11, 0.1), rgba(0, 0, 0, 0.8));
  padding: 4rem 2rem;
  text-align: center;
}

.hero-content {
  max-width: 800px;
  margin: 0 auto;
}

.hero-title {
  font-size: 2.5rem;
  font-weight: 700;
  margin-bottom: 1.5rem;
  color: var(--text);
  line-height: 1.2;
}

.hero-description {
  font-size: 1.5rem;
  color: var(--text-muted);
  margin-bottom: 2rem;
}

@media (max-width: 768px) {
  .hero {
    padding: 3rem 1rem;
  }

  .hero-title {
    font-size: 1.75rem;
  }

  .hero-description {
    font-size: 1.125rem;
  }
}

.form-section {
  background: var(--bg-darker);
  padding: 4rem 2rem;
}

.form-container {
  max-width: 600px;
  margin: 0 auto;
  background: rgba(255, 255, 255, 0.03);
  padding: 3rem;
  border-radius: 16px;
  border: 1px solid var(--border);
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.4);
}

.form-title {
  font-size: 2rem;
  text-align: center;
  margin-bottom: 2rem;
  color: var(--primary);
}

.quote-form {
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
}

.form-group {
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
}

.form-group label {
  font-size: 1rem;
  font-weight: 500;
  color: var(--text);
}

.input-example {
  font-size: 0.875rem;
  color: var(--text-muted);
  font-weight: 400;
}

.form-group input {
  padding: 1rem;
  font-size: 1rem;
  background: rgba(255, 255, 255, 0.05);
  border: 2px solid var(--border);
  border-radius: 8px;
  color: var(--text);
  transition: all 0.3s ease;
}

.form-group input:focus {
  outline: none;
  border-color: var(--primary);
  background: rgba(255, 255, 255, 0.08);
  box-shadow: 0 0 0 3px rgba(184, 134, 11, 0.1);
}

.form-group input::placeholder {
  color: var(--text-muted);
}

.form-row {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 1.5rem;
}

.btn-submit {
  margin-top: 1rem;
  width: 100%;
}

.success-message {
  background: linear-gradient(135deg, #10b981, #059669);
  color: white;
  padding: 1rem;
  border-radius: 8px;
  text-align: center;
  margin-top: 1.5rem;
  font-weight: 500;
  animation: slideDown 0.3s ease-out;
}

@keyframes slideDown {
  from {
    opacity: 0;
    transform: translateY(-20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@media (max-width: 768px) {
  .form-section {
    padding: 3rem 1rem;
  }

  .form-container {
    padding: 2rem 1.5rem;
  }

  .form-title {
    font-size: 1.5rem;
  }

  .form-row {
    grid-template-columns: 1fr;
  }
}

.map-section {
  padding: 4rem 2rem;
  background: var(--bg-dark);
}

.map-title {
  font-size: 2rem;
  text-align: center;
  margin-bottom: 2rem;
  color: var(--primary);
}

.map-container {
  max-width: 800px;
  margin: 0 auto;
  border-radius: 12px;
  overflow: hidden;
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.5);
  border: 2px solid var(--border);
}

.map-container iframe {
  display: block;
  filter: grayscale(20%) contrast(1.2);
}

@media (max-width: 768px) {
  .map-section {
    padding: 3rem 1rem;
  }

  .map-title {
    font-size: 1.5rem;
  }

  .map-container iframe {
    height: 300px !important;
  }
}

.footer {
  background: linear-gradient(180deg, var(--bg-dark), var(--bg-darker));
  border-top: 2px solid var(--primary);
  padding: 3rem 2rem;
  margin-top: auto;
}

.footer-content {
  max-width: 1200px;
  margin: 0 auto;
  text-align: center;
}

.footer-address {
  font-style: normal;
  font-size: 1.125rem;
  line-height: 2;
  color: var(--text);
  margin-bottom: 1.5rem;
}

.footer-link {
  color: var(--primary);
  text-decoration: none;
  transition: color 0.3s ease;
}

.footer-link:hover {
  color: var(--primary-light);
  text-decoration: underline;
}

.footer-copyright {
  color: var(--text-muted);
  font-size: 0.875rem;
  padding-top: 1.5rem;
  border-top: 1px solid var(--border);
  margin: 0 auto;
  max-width: 800px;
}

@media (max-width: 768px) {
  .footer {
    padding: 2rem 1rem;
  }

  .footer-address {
    font-size: 1rem;
  }
}

