# Redireciona HTTP para HTTPS
server {
    listen 80;
    server_name cortinasbras.com.br www.cortinasbras.com.br;
    return 301 https://$server_name$request_uri;
}

# Servidor HTTPS
server {
    listen 443 ssl;
    server_name cortinasbras.com.br www.cortinasbras.com.br;

    ssl_certificate /etc/letsencrypt/live/cortinasbras.com.br/fullchain.pem;
    ssl_certificate_key /etc/letsencrypt/live/cortinasbras.com.br/privkey.pem;

    # Todas as requisições vão para o Flask (que serve o React build)
    location / {
        proxy_pass http://127.0.0.1:8000;
        proxy_set_header Host $host;
        proxy_set_header X-Real-IP $remote_addr;
        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
        proxy_set_header X-Forwarded-Proto $scheme;

        # Timeout aumentado para o gunicorn
        proxy_connect_timeout 120;
        proxy_send_timeout 120;
        proxy_read_timeout 120;
    }

    # Logs
    access_log /var/log/nginx/cortinasbras_access.log;
    error_log /var/log/nginx/cortinasbras_error.log;
}

